        } <p>else</p> {
          writeFile <p>file</p>: <p><p>"</p><p>target/parallel-test-excludes-</p><p><p>${</p>i<p>}</p></p><p>.txt</p><p>"</p></p>, <p>text</p>: split.list.join(<p><p>"</p><p>\n</p><p>"</p></p>)
          mavenInstall += <p><p>"</p><p> -Dsurefire.excludesFile=target/parallel-test-excludes-</p><p><p>${</p>i<p>}</p></p><p>.txt</p><p>"</p></p>
        }

        <p>/* Call the Maven build with tests. */</p>
        mvn mavenInstall

        <p>/* Archive the test results */</p>
        step([<p>$</p><p>class</p>: <p><p>'</p><p>JUnitResultArchiver</p><p>'</p></p>, <p>testResults</p>: <p><p>'</p><p>**/target/surefire-reports/TEST-*.xml</p><p>'</p></p>])
      }
    }
  }
  parallel testGroups
}

